import React, { Component } from "react";
import { connect } from "react-redux";

import store from "./../store/index";
import { FilterByPrice } from "../actions/index";

const mapDispatchToProps = dispatch => {
  return {
    FilterByPrice: article => dispatch(FilterByPrice(article))
  };
};

class FilterRangeClass extends Component {
  constructor(props) {
    super(props);
    this.state = { value: "" };
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    this.setState({ value: event.target.value });
    console.log(this.state.value);
    this.props.FilterByPrice({ value: this.state.value });
  }

  render() {
    console.log(store.getState());
    return (
      <form>
        <input
          type="range"
          min="0"
          max="40"
          value={this.state.value}
          onChange={this.handleChange}
        />
        <p className="text_sidebar"> {this.state.value} â‚¬</p>
      </form>
    );
  }
}

//on connecte la classe avec le mapDisptachToProps
const FilterRange = connect(
  null,
  mapDispatchToProps
)(FilterRangeClass);
export default FilterRange;
